import React, { useState, useEffect } from "react";
import questionData from "./QuestionsData"; // Make sure you import your questions data

export default function MCQs() {
  // Function to shuffle an array (used to shuffle both questions and options)
  const shuffleArray = (array) => {
    return array.sort(() => Math.random() - 0.5);
  };

  // Function to shuffle the questions and pick 20 random ones
  const getRandomQuestions = (questions, count) => {
    const shuffled = shuffleArray([...questions]);
    return shuffled.slice(0, count);
  };

  // State to store the shuffled questions
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [shuffledOptions, setShuffledOptions] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isQuizCompleted, setIsQuizCompleted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null); // to track if the answer is correct or not

  useEffect(() => {
    const shuffledQuestions = getRandomQuestions(questionData, 20);
    setQuestions(shuffledQuestions);
  }, []);

  useEffect(() => {
    if (questions.length > 0) {
      // Shuffle options for the current question
      setShuffledOptions(
        shuffleArray([...questions[currentQuestionIndex].options])
      );
    }
  }, [currentQuestionIndex, questions]);

  const handleAnswerClick = (option) => {
    setSelectedAnswer(option);
    const correctAnswer = questions[currentQuestionIndex].correctAnswer;

    // Check if the answer is correct
    if (option === correctAnswer) {
      setIsCorrect(true); // correct answer
    } else {
      setIsCorrect(false); // wrong answer
    }

    // Delay for showing feedback before moving to the next question
    setTimeout(() => {
      if (currentQuestionIndex + 1 < questions.length) {
        // Move to the next question
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedAnswer(null); // Reset the selected answer for the next question
        setIsCorrect(null); // Reset correctness state for the next question
      } else {
        // Quiz is completed
        setIsQuizCompleted(true);
      }
    }, 1000); // Delay before moving to the next question (1 second)
  };

  // Only show the current question if the questions array is loaded and quiz is not completed
  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="p-4 text-white bg-gray-900 h-screen flex flex-col items-center">
      {isQuizCompleted ? (
        <h1 className="text-3xl font-bold mb-4">Quiz Completed!</h1>
      ) : currentQuestion ? (
        <>
          <h1 className="text-3xl font-bold mb-4">
            {currentQuestion.question}
          </h1>
          <img
            src={currentQuestion.flag}
            alt="Country Flag"
            className="w-20 h-20 mb-4"
          />
          <div className="flex flex-col space-y-2">
            {shuffledOptions.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerClick(option)}
                className={`p-2 rounded-lg text-lg border-2 border-gray-300 hover:bg-gray-700 ${
                  selectedAnswer === option
                    ? isCorrect === null
                      ? "bg-gray-800" // While waiting for feedback
                      : isCorrect && option === currentQuestion.correctAnswer
                      ? "bg-green-500" // Correct answer
                      : "bg-red-500" // Incorrect answer
                    : "bg-gray-800"
                }`}
                disabled={selectedAnswer !== null} // Disable other buttons after an answer is selected
              >
                {option}
              </button>
            ))}
          </div>
        </>
      ) : (
        <p>Loading questions...</p>
      )}
    </div>
  );
}
