import React, { useState, useEffect } from "react";
import questionData from "./QuestionsData"; // Make sure you import your questions data

export default function MCQs() {
  // Function to shuffle the questions and pick 20 random ones
  const getRandomQuestions = (questions, count) => {
    // Shuffle the questions array
    const shuffled = [...questions].sort(() => Math.random() - 0.5);
    // Return the first 'count' number of questions
    return shuffled.slice(0, count);
  };

  // State to store the shuffled questions
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);

  useEffect(() => {
    // Set the questions to 20 random ones from questionData
    const shuffledQuestions = getRandomQuestions(questionData, 20);
    setQuestions(shuffledQuestions); // Update the questions state with 20 random questions
  }, []);

  const handleAnswerClick = (option) => {
    setSelectedAnswer(option);
    // Logic to handle the answer click, like moving to the next question
  };

  // Only show the current question if the questions array is loaded
  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="p-4 text-white bg-gray-900 h-screen flex flex-col items-center">
      {currentQuestion ? (
        <>
          <h1 className="text-3xl font-bold mb-4">
            {currentQuestion.question}
          </h1>
          <img
            src={currentQuestion.flag}
            alt="Country Flag"
            className="w-20 h-20 mb-4"
          />
          <div className="flex flex-col space-y-2">
            {currentQuestion.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerClick(option)}
                className={`p-2 rounded-lg text-lg border-2 border-gray-300 hover:bg-gray-700 ${
                  selectedAnswer === option ? "bg-green-500" : "bg-gray-800"
                }`}
              >
                {option}
              </button>
            ))}
          </div>
        </>
      ) : (
        <p>Loading questions...</p>
      )}
    </div>
  );
}
