import React, { useState } from "react";
import "./App.css";
import MCQs from "./components/MCQs";
import WF from "./img.jpg";

function App() {
  const [quizStarted, setQuizStarted] = useState(false); // State to track if the quiz has started

  const handleStartQuiz = () => {
    setQuizStarted(true);
  };

  return (
    <div className="App">
      {!quizStarted ? (
        <div className="welcome-section flex flex-col items-center justify-center h-screen bg-gray-900 text-white">
          <h1 className="text-2xl font-bold mb-4">
            Welcome! Lets guess the Capital Cities
          </h1>
          <img src={WF} alt="World Flags" className="w-80 h-auto mb-6" />
          <button
            onClick={handleStartQuiz}
            className="p-3 rounded-lg text-lg bg-blue-500 hover:bg-blue-700 text-white font-bold"
          >
            Start Quiz
          </button>
        </div>
      ) : (
        <MCQs />
      )}
    </div>
  );
}

export default App;
