// MCQPage.js
import React, { useState, useEffect } from "react";
import questionsData from "./QuestionsData";

export default function MCQPage() {
  const getRandomQuestions = (questions, count) => {
    // Shuffle the questions array
    const shuffled = [...questions].sort(() => Math.random() - 0.5);
    // Return the first 'count' number of questions
    return shuffled.slice(0, count);
  };

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [score, setScore] = useState(0);
  useEffect(() => {
    // Set the questions to 20 random ones from questionData
    setQuestions(getRandomQuestions(questionData, 20));
  }, []);

  const currentQuestion = questionsData[currentQuestionIndex];

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    if (answer === currentQuestion.correctAnswer) {
      setScore(score + 1);
    }

    // Move to the next question after a short delay
    setTimeout(() => {
      if (currentQuestionIndex + 1 < questionsData.length) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedAnswer("");
      } else {
        alert(`Quiz finished! Your score is ${score}/${questionsData.length}`);
      }
    }, 1000);
  };

  return (
    <div className="p-4 text-white bg-gray-900 h-screen flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-4">{currentQuestion.question}</h1>
      <img src={currentQuestion.flag} alt="" />
      <div className="flex flex-col space-y-2">
        {currentQuestion.options.map((option, index) => (
          <button
            key={index}
            onClick={() => handleAnswerClick(option)}
            className={`p-2 rounded-lg text-lg border-2 border-gray-300 hover:bg-gray-700 ${
              selectedAnswer === option ? "bg-green-500" : "bg-gray-800"
            }`}
          >
            {option}
          </button>
        ))}
      </div>
    </div>
  );
}
